mapper.select:
SELECT /* acmdCerfListSelect */ AA.apctOgan AS oganCd ,tpost.FN_GET_BASCD_NM('00001',AA.apctOgan) AS apctOgan ,(SELECT TMPLT_NM FROM tpost.TB_DOC_TMPLT_MGNT WHERE TMPLT_CD = AA.tmpltCd AND OGAN_CD = AA.apctOgan GROUP BY TMPLT_NM) AS tmpltCd ,DATE_FORMAT(AA.trnsDate, '%Y-%m-%d') AS trnsDate ,acmdCerfSeq AS acmdCerfSeq ,DATE_FORMAT(STR_TO_DATE(AA.apctDate, '%Y%m%d%s'), '%Y-%m-%d') AS apctDate ,IF(AA.bsrpCls = '100', CONCAT(SUBSTR(AA.ofapElctAddr,1,3), '-', SUBSTR(AA.ofapElctAddr,4,2), '-', SUBSTR(AA.ofapElctAddr,6,8)) , CONCAT(SUBSTR(AA.ofapElctAddr,1,3), '-', SUBSTR(AA.ofapElctAddr,4,3), '-', SUBSTR(AA.ofapElctAddr,7,2), '-', SUBSTR(AA.ofapElctAddr,9,5))) AS ofapElctAddr ,name AS name ,ciNum AS ciNum ,tpost.FN_GET_BASCD_NM('00007',AA.issCls) AS issCls ,apctCls AS apctCls ,dwldUrl AS dwldUrl ,RNUM AS rn FROM( SELECT M.OGAN_CD AS apctOgan ,M.TMPLT_CD AS tmpltCd ,M.TRANS_DT AS trnsDate ,M.ACMD_CERF_SEQ AS acmdCerfSeq ,M.APCT_DATE AS apctDate ,M.OFAP_ELCT_ADDR AS ofapElctAddr ,B.NAME AS name ,B.CI_NUM AS ciNum ,B.BSRP_CLS AS bsrpCls ,M.ISS_CLS AS issCls ,M.APCT_CLS AS apctCls ,IFNULL(M.DWLD_URL, '') AS dwldUrl ,@ROWNUM:=@ROWNUM+1 AS RNUM FROM tpost.TB_ACMD_CERF_MGNT M ,tpost.TB_OFAP_ELCT_ADDR A ,tpost.TB_RCVE_CI_INFO B ,(SELECT @ROWNUM:=0) TMP WHERE M.APCT_DATE BETWEEN #{inputRengStDt} AND #{inputRengClosDt} AND A.OFAP_ELCT_ADDR = M.OFAP_ELCT_ADDR AND A.CI_NUM = B.CI_NUM
AND M.ISS_CLS = #{inputIssCls}
AND OEA.NAME LIKE CONCAT('%', #{inputName}, '%')
AND M.OFAP_ELCT_ADDR LIKE CONCAT('%', #{inputOfapElctAddr}, '%')
AND B.CI_NUM LIKE CONCAT('%', #{inputCiNum}, '%')
)AA WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{lastIndex} ORDER BY AA.apctDate DESC

SELECT /* sndnCheck */
	OFAP_ELCT_ADDR AS ofapElctAddr
	,'Y'			AS acmdInfoCrtYn
FROM 
	tpost.TB_SNDN_SUCC_MSG
WHERE
	OGAN_CD = #{inputPopuOganCd}
AND TRANS_DT = DATE_FORMAT(#{inputPopuTrnsDate}, '%Y%m%d')
AND TMPLT_CD = #{inputPopuTmpltCd}
AND CI_NUM = #{inputPopuCiNum}

mapper.insert:
INSERT /* acmdCerfInsert */  
INTO tpost.TB_ACMD_CERF_MGNT ( 
	OGAN_CD
	,TMPLT_CD
	,TRANS_DT
	,ACMD_CERF_SEQ
	,OFAP_ELCT_ADDR	
	,CRT_DATE
	,MDF_DATE
	,CRTR
	,AMDR
	,APCT_CLS
	,APCT_DATE
	,ISS_CLS
	,DWLD_URL
)
VALUES
(
	#{inputPopuOganCd}
	,#{inputPopuTmpltCd}
	,#{inputPopuTrnsDate}
	,#{acmdCerfSeq}
	,#{inputPopuOfapElctAddr}
	,NOW()
	,NOW()
	,#{crtr}
	,#{amdr}
	,'0'
	,DATE_FORMAT(NOW(), '%Y%m%d') 
	,#{issCls}
	,null
)

INSERT /* acmdCerfHisInsert */  
INTO tpost.TB_ACMD_CERF_MGNT_HIS ( 
	ISS_CLS
	,OGAN_CD
	,TMPLT_CD
	,TRANS_DT
	,ACMD_CERF_SEQ
	,OFAP_ELCT_ADDR	
	,CRT_DATE
	,MDF_DATE
	,CRTR
	,AMDR
	,APCT_CLS
	,APCT_DATE
	,DWLD_URL
)
VALUES
(
	#{issCls}
	,#{inputPopuOganCd}
	,#{inputPopuTmpltCd}
	,#{inputPopuTrnsDate}
	,#{acmdCerfSeq}
	,#{inputPopuOfapElctAddr}
	,NOW()
	,NOW()
	,#{crtr}
	,#{amdr}
	,'0'
	,DATE_FORMAT(NOW(), '%Y%m%d') 
	,null
)

mapper.update:
UPDATE /* acmdCerfUpdate */
	tpost.TB_ACMD_CERF_MGNT
SET 
	ISS_CLS = #{issCls}
	,DWLD_URL = null
	,MDF_DATE = NOW()
	,AMDR = #{amdr}
WHERE 
	ACMD_CERF_SEQ = #{acmdCerfSeq}
AND OFAP_ELCT_ADDR = #{inputPopuOfapElctAddr}
AND TMPLT_CD = #{inputPopuTmpltCd}
AND OGAN_CD = #{inputPopuOganCd}
AND TRANS_DT = #{inputPopuTrnsDate}

mapper.select:
SELECT /* getMaxSeq */
		IFNULL(MAX(ACMD_CERF_SEQ ) + 1, 1) AS pk
FROM 
	tpost.TB_ACMD_CERF_MGNT
WHERE
	OGAN_CD = #{inputPopuOganCd}
AND TMPLT_CD = #{inputPopuTmpltCd}
AND TRANS_DT = DATE_FORMAT(#{inputPopuTrnsDate}, '%Y%m%d')
AND OFAP_ELCT_ADDR = #{inputPopuOfapElctAddr}
